import React, { useEffect, useState } from "react";
import axios from "axios";

function LiveRateView() {
  const [rates, setRates] = useState([]);

  useEffect(() => {
    axios.get("/api/admin/live-rates")
      .then(res => setRates(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="live-rate-view">
      <h1>Live Rates</h1>
      {rates.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Daily Market Rate</th>
              <th>Company Buying Rate</th>
            </tr>
          </thead>
          <tbody>
            {rates.map((rate, idx) => (
              <tr key={idx}>
                <td>{new Date(rate.date).toLocaleDateString()}</td>
                <td>{rate.marketRate}</td>
                <td>{rate.companyRate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No rates available today.</p>
      )}
    </div>
  );
}

export default LiveRateView;
