import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminLeavePage = () => {
    const [leaves, setLeaves] = useState([]);
    const token = localStorage.getItem('token');

    const fetchLeaves = async () => {
        const res = await axios.get('http://localhost:5000/api/leave/all', {
            headers: { Authorization: `Bearer ${token}` }
        });
        setLeaves(res.data);
    };

    useEffect(() => { fetchLeaves(); }, []);

    const updateStatus = async (id, status) => {
        try {
            await axios.put(`http://localhost:5000/api/leave/${id}/status`, { status }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            fetchLeaves();
        } catch (err) {
            alert(err.response.data.message || 'Error updating leave status');
        }
    };

    return (
        <div>
            <h2>All Leave Requests</h2>
            <table border="1">
                <thead>
                    <tr>
                        <th>Staff</th>
                        <th>Type</th>
                        <th>Start</th>
                        <th>End</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {leaves.map(l => (
                        <tr key={l._id}>
                            <td>{l.staff.name}</td>
                            <td>{l.leaveType}</td>
                            <td>{new Date(l.startDate).toLocaleDateString()}</td>
                            <td>{new Date(l.endDate).toLocaleDateString()}</td>
                            <td>{l.status}</td>
                            <td>
                                {l.status === 'pending' && (
                                    <>
                                        <button onClick={() => updateStatus(l._id, 'approved')}>Approve</button>
                                        <button onClick={() => updateStatus(l._id, 'rejected')}>Reject</button>
                                    </>
                                )}
                                {l.status !== 'pending' && <span>â€”</span>}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminLeavePage;
