import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate, Link } from 'react-router-dom';

const ResetPasswordPage = () => {
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const { token } = useParams();
    const navigate = useNavigate();

    const validateForm = () => {
        const passwordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,}$/;
        if (!password || !confirmPassword) {
            setError('Please fill out both fields.');
            return false;
        }
        if (password !== confirmPassword) {
            setError('Passwords do not match.');
            return false;
        }
        if (!passwordRegex.test(password)) {
            setError('Password must have at least one uppercase, one lowercase, one number, and be 6+ characters long.');
            return false;
        }
        setError('');
        return true;
    };

    const onSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');

        if (validateForm()) {
            setIsLoading(true);
            try {
                const response = await axios.put(
                    `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/reset-password/${token}`, 
                    { password }
                );
                setMessage(response.data.message || 'Password has been successfully reset. You can now log in.');
                setTimeout(() => navigate('/login'), 3000);
            } catch (err) {
                setError(err.response?.data?.message || 'An error occurred while resetting your password.');
            } finally {
                setIsLoading(false);
            }
        }
    };

    return (
        <div className="auth-wrapper">
            <div className="form-container">
                <h2>Reset Your Password</h2>
                {message && <div className="success-message">{message}</div>}
                {error && <div className="error-message">{error}</div>}
                <form onSubmit={onSubmit}>
                    <div className="input-group">
                        <input
                            className="form-input"
                            type="password"
                            placeholder="Enter new password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            disabled={isLoading}
                        />
                    </div>
                    <div className="input-group">
                        <input
                            className="form-input"
                            type="password"
                            placeholder="Confirm new password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            required
                            disabled={isLoading}
                        />
                    </div>
                    <button 
                        className="form-button" 
                        type="submit"
                        disabled={isLoading}
                    >
                        {isLoading ? 'Resetting...' : 'Reset Password'}
                    </button>
                </form>
                <p className="form-text">
                    Remember your password? <Link to="/login">Login here</Link>
                </p>
            </div>
        </div>
    );
};

export default ResetPasswordPage;
