import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ManageRates.css";

const ManageRates = () => {
  const [rates, setRates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [showAddModal, setShowAddModal] = useState(false);
  const [newRate, setNewRate] = useState({
    rate: "",
    effectiveDate: "",
    notes: ""
  });
  const [editingRate, setEditingRate] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);

  useEffect(() => {
    fetchRates();
  }, []);

  const fetchRates = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL || "http://localhost:5000"}/api/rates/admin/all`,
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      
      if (response.data.success) {
        setRates(response.data.rates);
      }
    } catch (err) {
      setError("Failed to fetch rates");
      console.error("Error fetching rates:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleAddRate = async () => {
    try {
      if (!newRate.rate || !newRate.effectiveDate) {
        setError("Please fill in all required fields");
        return;
      }

      const token = localStorage.getItem("token");
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL || "http://localhost:5000"}/api/rates/admin/add`,
        newRate,
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );

      if (response.data.success) {
        setShowAddModal(false);
        setNewRate({ rate: "", effectiveDate: "", notes: "" });
        fetchRates();
        setError("");
      }
    } catch (err) {
      setError(err.response?.data?.message || "Failed to add rate");
    }
  };

  const handleEditRate = async () => {
    try {
      if (!editingRate.rate || !editingRate.effectiveDate) {
        setError("Please fill in all required fields");
        return;
      }

      const token = localStorage.getItem("token");
      const response = await axios.put(
        `${process.env.REACT_APP_API_URL || "http://localhost:5000"}/api/rates/admin/${editingRate._id}`,
        editingRate,
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );

      if (response.data.success) {
        setShowEditModal(false);
        setEditingRate(null);
        fetchRates();
        setError("");
      }
    } catch (err) {
      setError(err.response?.data?.message || "Failed to update rate");
    }
  };

  const handleDeleteRate = async (rateId) => {
    if (!window.confirm("Are you sure you want to delete this rate?")) {
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await axios.delete(
        `${process.env.REACT_APP_API_URL || "http://localhost:5000"}/api/rates/admin/${rateId}`,
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );

      if (response.data.success) {
        fetchRates();
        setError("");
      }
    } catch (err) {
      setError(err.response?.data?.message || "Failed to delete rate");
    }
  };

  const openEditModal = (rate) => {
    setEditingRate({ ...rate });
    setShowEditModal(true);
  };

  const closeModals = () => {
    setShowAddModal(false);
    setShowEditModal(false);
    setNewRate({ rate: "", effectiveDate: "", notes: "" });
    setEditingRate(null);
    setError("");
  };

  if (loading) {
    return (
      <div className="manage-rates">
        <div className="loading">Loading rates...</div>
      </div>
    );
  }

  return (
    <div className="manage-rates">
      <div className="page-header">
        <h1>Rate Management</h1>
        <p>Manage and control latex rates for the company</p>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="actions-section">
        <button 
          className="add-rate-btn"
          onClick={() => setShowAddModal(true)}
        >
          <i className="fas fa-plus"></i>
          Add New Rate
        </button>
      </div>

      <div className="rates-table-container">
        <h2>Current Rates</h2>
        {rates.length === 0 ? (
          <div className="no-data">No rates found</div>
        ) : (
          <table className="rates-table">
            <thead>
              <tr>
                <th>Rate (₹/kg)</th>
                <th>Effective Date</th>
                <th>Status</th>
                <th>Notes</th>
                <th>Created On</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {rates.map((rate) => (
                <tr key={rate._id}>
                  <td className="rate-value">₹{rate.rate}</td>
                  <td>{new Date(rate.effectiveDate).toLocaleDateString()}</td>
                  <td>
                    <span className={`status-badge ${rate.status || 'active'}`}>
                      {rate.status || 'Active'}
                    </span>
                  </td>
                  <td className="notes-cell">
                    {rate.notes || 'No notes'}
                  </td>
                  <td>{new Date(rate.createdAt).toLocaleDateString()}</td>
                  <td className="actions-cell">
                    <button 
                      className="edit-btn"
                      onClick={() => openEditModal(rate)}
                    >
                      <i className="fas fa-edit"></i>
                      Edit
                    </button>
                    <button 
                      className="delete-btn"
                      onClick={() => handleDeleteRate(rate._id)}
                    >
                      <i className="fas fa-trash"></i>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      {/* Add Rate Modal */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Add New Rate</h3>
              <button onClick={closeModals} className="close-btn">
                <i className="fas fa-times"></i>
              </button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>Rate (₹/kg):</label>
                <input
                  type="number"
                  step="0.01"
                  value={newRate.rate}
                  onChange={(e) => setNewRate({ ...newRate, rate: e.target.value })}
                  placeholder="Enter rate per kg"
                  required
                />
              </div>
              <div className="form-group">
                <label>Effective Date:</label>
                <input
                  type="date"
                  value={newRate.effectiveDate}
                  onChange={(e) => setNewRate({ ...newRate, effectiveDate: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label>Notes:</label>
                <textarea
                  value={newRate.notes}
                  onChange={(e) => setNewRate({ ...newRate, notes: e.target.value })}
                  placeholder="Optional notes about this rate"
                  rows="3"
                />
              </div>
            </div>
            <div className="modal-footer">
              <button onClick={closeModals} className="cancel-btn">
                Cancel
              </button>
              <button onClick={handleAddRate} className="save-btn">
                Add Rate
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Rate Modal */}
      {showEditModal && editingRate && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Edit Rate</h3>
              <button onClick={closeModals} className="close-btn">
                <i className="fas fa-times"></i>
              </button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>Rate (₹/kg):</label>
                <input
                  type="number"
                  step="0.01"
                  value={editingRate.rate}
                  onChange={(e) => setEditingRate({ ...editingRate, rate: e.target.value })}
                  placeholder="Enter rate per kg"
                  required
                />
              </div>
              <div className="form-group">
                <label>Effective Date:</label>
                <input
                  type="date"
                  value={editingRate.effectiveDate}
                  onChange={(e) => setEditingRate({ ...editingRate, effectiveDate: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label>Notes:</label>
                <textarea
                  value={editingRate.notes}
                  onChange={(e) => setEditingRate({ ...editingRate, notes: e.target.value })}
                  placeholder="Optional notes about this rate"
                  rows="3"
                />
              </div>
            </div>
            <div className="modal-footer">
              <button onClick={closeModals} className="cancel-btn">
                Cancel
              </button>
              <button onClick={handleEditRate} className="save-btn">
                Update Rate
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageRates;
