import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const RegisterPage = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phoneNumber: '',
        password: '',
    });
    const [errors, setErrors] = useState({});
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const { name, email, phoneNumber, password } = formData;

    const onChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
        // Clear error when user starts typing
        if (errors[name]) {
            setErrors({ ...errors, [name]: '' });
        }
    };

    // Real-time validation for name (no spaces, alphanumeric only)
    const validateName = (name) => {
        if (!name.trim()) {
            return 'Name is required.';
        }
        if (name.includes(' ')) {
            return 'Name cannot contain spaces.';
        }
        if (!/^[a-zA-Z0-9]+$/.test(name)) {
            return 'Name must contain only letters and numbers (no special characters).';
        }
        if (name.length < 2) {
            return 'Name must be at least 2 characters long.';
        }
        if (name.length > 50) {
            return 'Name must be less than 50 characters.';
        }
        return '';
    };

    // Real-time validation for phone number
    const validatePhoneNumber = (phone) => {
        if (!phone.trim()) {
            return 'Phone number is required.';
        }
        
        // Remove all non-digit characters for validation
        const cleanPhone = phone.replace(/\D/g, '');
        
        // Check if it's all zeros
        if (/^0+$/.test(cleanPhone)) {
            return 'Phone number cannot be all zeros.';
        }
        
        // Check for country code patterns
        if (cleanPhone.startsWith('91') && cleanPhone.length === 12) {
            // Indian number with country code: +91 9876543210
            const numberWithoutCode = cleanPhone.substring(2);
            if (!/^[6-9]\d{9}$/.test(numberWithoutCode)) {
                return 'Invalid Indian mobile number format.';
            }
        } else if (cleanPhone.length === 10) {
            // 10-digit number without country code
            if (!/^[6-9]\d{9}$/.test(cleanPhone)) {
                return 'Please provide a valid 10-digit Indian mobile number.';
            }
        } else if (cleanPhone.length === 11 && cleanPhone.startsWith('0')) {
            // Number starting with 0
            const numberWithoutZero = cleanPhone.substring(1);
            if (!/^[6-9]\d{9}$/.test(numberWithoutZero)) {
                return 'Please provide a valid 10-digit Indian mobile number.';
            }
        } else {
            return 'Please provide a valid phone number (10 digits or +91 followed by 10 digits).';
        }
        
        return '';
    };

    // Real-time validation for password
    const validatePassword = (password) => {
        if (!password) {
            return 'Password is required.';
        }
        if (password.length < 6) {
            return 'Password must be at least 6 characters long.';
        }
        if (!/(?=.*\d)/.test(password)) {
            return 'Password must contain at least one number.';
        }
        if (!/(?=.*[a-z])/.test(password)) {
            return 'Password must contain at least one lowercase letter.';
        }
        if (!/(?=.*[A-Z])/.test(password)) {
            return 'Password must contain at least one uppercase letter.';
        }
        if (!/^[a-zA-Z0-9!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]+$/.test(password)) {
            return 'Password contains invalid characters.';
        }
        return '';
    };

    // Real-time validation for email
    const validateEmail = (email) => {
        if (!email.trim()) {
            return 'Email is required.';
        }
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(email)) {
            return 'Please enter a valid email address.';
        }
        return '';
    };

    const validateForm = () => {
        const newErrors = {};
        
        // Validate each field
        const nameError = validateName(name);
        const emailError = validateEmail(email);
        const phoneError = validatePhoneNumber(phoneNumber);
        const passwordError = validatePassword(password);
        
        if (nameError) newErrors.name = nameError;
        if (emailError) newErrors.email = emailError;
        if (phoneError) newErrors.phoneNumber = phoneError;
        if (passwordError) newErrors.password = passwordError;
        
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const onSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        
        if (validateForm()) {
            try {
                // Clean phone number before sending
                const cleanPhoneNumber = phoneNumber.replace(/\D/g, '');
                const finalPhoneNumber = cleanPhoneNumber.startsWith('91') && cleanPhoneNumber.length === 12 
                    ? cleanPhoneNumber.substring(2) 
                    : cleanPhoneNumber.startsWith('0') 
                        ? cleanPhoneNumber.substring(1) 
                        : cleanPhoneNumber;

                const registrationData = {
                    ...formData,
                    phoneNumber: finalPhoneNumber
                };

                const res = await axios.post(
                    `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/register`,
                    registrationData
                );
                alert('Registration successful! Please log in.');
                navigate('/login');
            } catch (err) {
                console.error('Registration error:', err);
                setErrors({
                    general: err.response?.data?.message || 'An error occurred during registration.'
                });
            }
        }
        setIsLoading(false);
    };

    return (
        <div className="auth-wrapper">
            <div className="form-container">
                <h2>Register Your Account</h2>
                {errors.general && <div className="error-message">{errors.general}</div>}
                <form onSubmit={onSubmit}>
                    <div className="input-group">
                        <input
                            className={`form-input ${errors.name ? 'error' : ''}`}
                            type="text"
                            placeholder="Full Name (no spaces, alphanumeric only)"
                            name="name"
                            value={name}
                            onChange={onChange}
                            maxLength={50}
                        />
                        {errors.name && <div className="field-error">{errors.name}</div>}
                    </div>
                    <div className="input-group">
                        <input
                            className={`form-input ${errors.email ? 'error' : ''}`}
                            type="email"
                            placeholder="Email Address"
                            name="email"
                            value={email}
                            onChange={onChange}
                        />
                        {errors.email && <div className="field-error">{errors.email}</div>}
                    </div>
                    <div className="input-group">
                        <input
                            className={`form-input ${errors.phoneNumber ? 'error' : ''}`}
                            type="text"
                            placeholder="Phone Number (10 digits or +91 9876543210)"
                            name="phoneNumber"
                            value={phoneNumber}
                            onChange={onChange}
                            maxLength={15}
                        />
                        {errors.phoneNumber && <div className="field-error">{errors.phoneNumber}</div>}
                    </div>
                    <div className="input-group">
                        <input
                            className={`form-input ${errors.password ? 'error' : ''}`}
                            type="password"
                            placeholder="Password (min 6 chars, uppercase, lowercase, number)"
                            name="password"
                            value={password}
                            onChange={onChange}
                        />
                        {errors.password && <div className="field-error">{errors.password}</div>}
                    </div>
                    <button 
                        className="form-button" 
                        type="submit"
                        disabled={isLoading}
                    >
                        {isLoading ? 'Registering...' : 'Register'}
                    </button>
                </form>
                <p className="form-text">
                    Already have an account? <Link to="/login">Login here</Link>
                </p>
            </div>
        </div>
    );
};

export default RegisterPage;
