import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';
import { useAuth } from '../../context/AuthContext';

const LoginPage = () => {
    const [formData, setFormData] = useState({ email: '', password: '' });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    const { login, googleSignIn } = useAuth();

    const { email, password } = formData;
    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const validateForm = () => {
        const emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;

        if (!email || !password) {
            setError('Please enter both email and password.');
            return false;
        }
        if (!emailRegex.test(email)) {
            setError('Please enter a valid email address.');
            return false;
        }
        setError('');
        return true;
    };
    
    const handleGoogleSignInSuccess = async (credentialResponse) => {
        try {
            setLoading(true);
            setError('');
            
            const result = await googleSignIn(credentialResponse.credential);
            
            if (result.user.role === 'admin') {
                navigate('/admin/home');
            } else {
                navigate('/user/home');
            }
        } catch (err) {
            setError('Google Sign-In failed. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const onSubmit = async (e) => {
        e.preventDefault();
        if (validateForm()) {
            try {
                setLoading(true);
                setError('');
                
                const result = await login(email, password);
                
                if (result.user.role === 'admin') {
                    navigate('/admin/home');
                } else {
                    navigate('/user/home');
                }
            } catch (err) {
                setError(err.response?.data?.message || err.message || 'An error occurred during login.');
            } finally {
                setLoading(false);
            }
        }
    };
    

    return (
        <div className="auth-wrapper">
            <div className="form-container">
                
                {/* âœ… Company Logo */}
                <div className="logo-container">
                    <img 
                        src="/images/logo.png" 
                        alt="Company Logo" 
                        className="company-logo"
                    />
                </div>

                <h2>Login to Your Account</h2>
                {error && <div className="error-message">{error}</div>}
                <form onSubmit={onSubmit}>
                    <div className="input-group">
                        <input className="form-input" type="email" placeholder="Email Address" name="email" value={email} onChange={onChange} required />
                    </div>
                    <div className="input-group">
                        <input className="form-input" type="password" placeholder="Password" name="password" value={password} onChange={onChange} required />
                    </div>
                    <button className="form-button" type="submit" disabled={loading}>
                        {loading ? 'Logging in...' : 'Login'}
                    </button>
                </form>
 
                <div style={{ textAlign: 'center', margin: '20px 0', color: '#888' }}>OR</div>
                
                <GoogleLogin
                    onSuccess={handleGoogleSignInSuccess}
                    onError={() => setError('Google Sign-In failed. Please try again.')}
                    disabled={loading}
                />

                <div className="auth-links">
                    <Link to="/forgot-password">Forgot Password?</Link>
                    <span>Don't have an account? <Link to="/register">Register here</Link></span>
                </div>
            </div>
        </div>
    );
};

export default LoginPage;
