import React from 'react';
import { Routes, Route } from 'react-router-dom';
import './App.css';

// Layouts and Protection
import DashboardLayout from './layouts/DashboardLayout';
import AdminDashboardLayout from './layouts/AdminDashboardLayout';
import ProtectedRoute from './components/common/ProtectedRoute';
import AdminProtectedRoute from './components/common/AdminProtectedRoute';

// Public Pages
import HomePage from './pages/HomePage';
import LoginPage from './pages/auth/LoginPage';
import RegisterPage from './pages/auth/RegisterPage';
// In App.js, inside your <Routes>
import ForgotPasswordPage from './pages/auth/ForgotPasswordPage';
import ResetPasswordPage from './pages/auth/ResetPasswordPage';


// User Dashboard Pages
import UserHome from './pages/user_dashboard/UserHome';
import MySubmissions from './pages/user_dashboard/MySubmissions';
import Profile from './pages/user_dashboard/Profile';
import SubmitRequest from './pages/user_dashboard/SubmitRequest';

// Admin Dashboard Pages
import AdminHome from './pages/admin_dashboard/AdminHome';
import BillRequests from './pages/admin_dashboard/BillRequests';
import ManageShifts from './pages/admin_dashboard/ManageShifts';

function App() {
  return (
    <Routes>
      {/* Public Routes */}
      <Route path="/" element={<HomePage />} />
      <Route path="/login" element={<LoginPage />} />
      <Route path="/register" element={<RegisterPage />}
       />

      {/* User Dashboard Routes */}
      <Route path="/user/home" element={<ProtectedRoute><DashboardLayout><UserHome /></DashboardLayout></ProtectedRoute>} />
      <Route path="/user/submissions" element={<ProtectedRoute><DashboardLayout><MySubmissions /></DashboardLayout></ProtectedRoute>} />
      <Route path="/user/profile" element={<ProtectedRoute><DashboardLayout><Profile /></DashboardLayout></ProtectedRoute>} />
      <Route path="/user/submit-request" element={<ProtectedRoute><DashboardLayout><SubmitRequest /></DashboardLayout></ProtectedRoute>} />

      {/* Admin Dashboard Routes */}
      <Route 
        path="/admin/home" 
        element={
          <AdminProtectedRoute>
            <AdminDashboardLayout>
              <AdminHome />
            </AdminDashboardLayout>
          </AdminProtectedRoute>
        } 
      />
      <Route 
        path="/admin/bills" 
        element={
          <AdminProtectedRoute>
            <AdminDashboardLayout>
              <BillRequests />
            </AdminDashboardLayout>
          </AdminProtectedRoute>
        } 
      />
      <Route 
        path="/admin/shifts" 
        element={
          <AdminProtectedRoute>
            <AdminDashboardLayout>
              <ManageShifts />
            </AdminDashboardLayout>
          </AdminProtectedRoute>
        } 
      />
    </Routes>
  );
}

export default App;
