import React, { useEffect, useState } from 'react';
import './HomePage.css'; // reuse styles or make a new CSS if you want

const LiveRates = () => {
  const [data, setData] = useState(null);
  const [err, setErr] = useState('');

  const fetchRates = async () => {
    try {
      setErr('');
      const res = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/rates`);
      if (!res.ok) throw new Error('Failed to fetch rates');
      const json = await res.json();
      setData(json);
    } catch (e) {
      setErr(e.message || 'Error loading live rates');
    }
  };

  useEffect(() => {
    fetchRates();
    // Auto-refresh every 60s (optional)
    const t = setInterval(fetchRates, 60000);
    return () => clearInterval(t);
  }, []);

  return (
    <section className="features-section" style={{ paddingTop: '4rem' }}>
      <h2 className="section-title">Live Price Display</h2>
      <p className="section-subtitle">Daily market rate and company buying rate</p>

      {err && <div className="error-message" style={{ maxWidth: 520, margin: '1rem auto' }}>{err}</div>}

      {data ? (
        <div className="features-grid" style={{ maxWidth: 720, margin: '0 auto' }}>
          <div className="feature-card">
            <i className="fas fa-chart-line feature-icon"></i>
            <h3>Market Rate</h3>
            <p style={{ fontSize: '1.4rem', fontWeight: 700 }}>{data.marketRate}</p>
          </div>
          <div className="feature-card">
            <i className="fas fa-tag feature-icon"></i>
            <h3>Company Buying Rate</h3>
            <p style={{ fontSize: '1.4rem', fontWeight: 700 }}>{data.companyRate}</p>
          </div>
          <div className="feature-card">
            <i className="fas fa-clock feature-icon"></i>
            <h3>Last Updated</h3>
            <p>{new Date(data.updatedAt).toLocaleString()}</p>
            <small style={{ color: '#888' }}>Source: {data.source || 'manual'}</small>
          </div>
        </div>
      ) : (
        !err && <p style={{ textAlign: 'center' }}>Loading latest ratesâ€¦</p>
      )}
    </section>
  );
};

export default LiveRates;
