import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { validateStaffRegistration } from '../../utils/validation';

const StaffRegisterPage = () => {
  const [form, setForm] = useState({ name: '', email: '', phoneNumber: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [assignedStaffId, setAssignedStaffId] = useState('');
  const navigate = useNavigate();

  // Fetch next staff ID preview on mount
  useEffect(() => {
    const fetchNextId = async () => {
      try {
        const base = process.env.REACT_APP_API_URL || 'http://localhost:5000';
        const { data } = await axios.get(`${base}/api/auth/next-staff-id`);
        setAssignedStaffId(data.staffId);
      } catch (e) {
        // ignore preview failure
      }
    };
    fetchNextId();
  }, []);

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const submit = async (e) => {
    e.preventDefault();
    setError('');

    // Enhanced validation
    const validationErrors = validateStaffRegistration(form);
    if (Object.keys(validationErrors).length > 0) {
      const errorMessages = Object.values(validationErrors).join(', ');
      setError(`Validation failed: ${errorMessages}`);
      return;
    }

    try {
      setLoading(true);
      console.log('Attempting staff registration with data:', form);
      const payload = { name: form.name, email: form.email, phoneNumber: form.phoneNumber };
      const base = process.env.REACT_APP_API_URL || 'http://localhost:5000';
      const { data } = await axios.post(`${base}/api/auth/register-staff`, payload);
      console.log('Registration successful:', data);
      // Do not auto-login; show assigned Staff ID then send user to staff login screen
      const newId = data.user?.staffId || '';
      setAssignedStaffId(newId);
      navigate('/staff/login', { replace: true, state: { staffId: newId } });
    } catch (err) {
      console.error('Registration error:', err);

      // Handle different error types
      if (err.response?.data?.errors) {
        // Validation errors from server
        const errorMessages = Object.values(err.response.data.errors).join(', ');
        setError(`Validation failed: ${errorMessages}`);
      } else if (err.response?.data?.message) {
        // Server error message
        setError(err.response.data.message);
      } else if (err.code === 'NETWORK_ERROR' || !err.response) {
        // Network error
        setError('Network error. Please check your connection and try again.');
      } else {
        // Generic error
        setError('Registration failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-wrapper ripple-cursor staff-theme">
      <div className="form-container">
        <div className="logo-container">
          <img src="/images/logo.png" alt="Company Logo" className="company-logo" />
        </div>
        <div className="back-row">
          <Link to="/" className="back-link">‚Üê Back to Home</Link>
        </div>
        <h2>Register Staff</h2>
        {assignedStaffId && (
          <div className="success-message">Your Staff ID: <strong>{assignedStaffId}</strong></div>
        )}
        {error && <div className="error-message">{error}</div>}
        <form onSubmit={submit}>
          <div className="input-group">
            <input className="form-input" name="name" placeholder="Full Name" value={form.name} onChange={onChange} required />
          </div>
          <div className="input-group">
            <input className="form-input" type="email" name="email" placeholder="Email Address" value={form.email} onChange={onChange} required />
          </div>
          <div className="input-group">
            <input className="form-input" name="phoneNumber" placeholder="Phone Number" value={form.phoneNumber} onChange={onChange} required />
          </div>
          {/* Password removed for staff registration; password will be the Staff ID and sent via email */}
          {assignedStaffId && (
            <div className="input-group">
              <input className="form-input" value={assignedStaffId} readOnly />
            </div>
          )}
          <button className="form-button" type="submit" disabled={loading}>{loading ? 'Creating...' : 'Create Staff Account'}</button>
        </form>
        <div className="auth-links">
          <span>Already staff? <Link to="/staff/login">Login here</Link></span>
          <span><Link to="/auth/select">Back to selection</Link></span>
        </div>
      </div>
    </div>
  );
};

export default StaffRegisterPage;
