import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';

const LoginPage = () => {
    const [formData, setFormData] = useState({ email: '', password: '' });
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const { email, password } = formData;
    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    // NEW: Validation function
    const validateForm = () => {
        const emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;

        if (!email || !password) {
            setError('Please enter both email and password.');
            return false;
        }
        if (!emailRegex.test(email)) {
            setError('Please enter a valid email address.');
            return false;
        }
        setError('');
        return true;
    };
    
    const handleGoogleSignInSuccess = async (credentialResponse) => {
        try {
            const res = await axios.post('/api/auth/google-signin', {
                token: credentialResponse.credential,
            });

            localStorage.setItem('token', res.data.token);
            localStorage.setItem('user', JSON.stringify(res.data.user));

            if (res.data.user.role === 'admin') {
                navigate('/admin/home');
            } else {
                navigate('/user/home');
            }
        } catch (err) {
            setError('Google Sign-In failed. Please try again.');
        }
    };

    const onSubmit = async (e) => {
        e.preventDefault();
        // Use the new validation function
        if (validateForm()) {
            try {
                const res = await axios.post('/api/auth/login', formData);
                localStorage.setItem('token', res.data.token);
                localStorage.setItem('user', JSON.stringify(res.data.user));
                if (res.data.user.role === 'admin') {
                    navigate('/admin/home');
                } else {
                    navigate('/user/home');
                }
            } catch (err) {
                setError(err.response?.data?.message || 'An error occurred during login.');
            }
        }
    };

    return (
        <div className="auth-wrapper">
            <div className="form-container">
                <h2>Login to Your Account</h2>
                {error && <div className="error-message">{error}</div>}
                <form onSubmit={onSubmit}>
                    <div className="input-group">
                        <input className="form-input" type="email" placeholder="Email Address" name="email" value={email} onChange={onChange} required />
                    </div>
                    <div className="input-group">
                        <input className="form-input" type="password" placeholder="Password" name="password" value={password} onChange={onChange} required />
                    </div>
                    <button className="form-button" type="submit">Login</button>
                </form>

                <div style={{ textAlign: 'center', margin: '20px 0', color: '#888' }}>OR</div>
                
                <GoogleLogin
                    onSuccess={handleGoogleSignInSuccess}
                    onError={() => {
                        setError('Google Sign-In failed. Please try again.');
                    }}
                    useOneTap
                />

                <p className="form-text">
                    Don't have an account? <Link to="/register">Register here</Link>
                </p>
            </div>
        </div>
    );
};

export default LoginPage;
