import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BillRequests = () => {
    const [bills, setBills] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const fetchBills = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const { data } = await axios.get('/api/admin/bills', config);
            setBills(data);
        } catch (err) {
            setError('Failed to fetch bill requests.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchBills();
    }, []);

    const handleApprove = async (id) => {
        try {
            const token = localStorage.getItem('token');
            const config = { headers: { Authorization: `Bearer ${token}` } };
            await axios.put(`/api/admin/bills/${id}/approve`, {}, config);
            // Refresh the list to show the status change
            fetchBills();
        } catch (err) {
            alert('Failed to approve the bill.');
        }
    };

    const handleReject = async (id) => {
        try {
            const token = localStorage.getItem('token');
            const config = { headers: { Authorization: `Bearer ${token}` } };
            await axios.put(`/api/admin/bills/${id}/reject`, {}, config);
            // Refresh the list
            fetchBills();
        } catch (err) {
            alert('Failed to reject the bill.');
        }
    };

    if (loading) return <p>Loading...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    return (
        <div>
            <h2>Manage Bill Submissions</h2>
            <table className="dashboard-table">
                <thead>
                    <tr>
                        <th>Supplier Name</th>
                        <th>Date</th>
                        <th>Volume (L)</th>
                        <th>Amount (â‚¹)</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {bills.length > 0 ? bills.map(bill => (
                        <tr key={bill._id}>
                            <td>{bill.supplier ? bill.supplier.name : 'N/A'}</td>
                            <td>{new Date(bill.createdAt).toLocaleDateString()}</td>
                            <td>{bill.latexVolume}</td>
                            <td>{bill.calculatedAmount.toFixed(2)}</td>
                            <td>
                                <span className={`status-badge status-${bill.status}`}>
                                    {bill.status}
                                </span>
                            </td>
                            <td>
                                {bill.status === 'pending' && (
                                    <>
                                        <button onClick={() => handleApprove(bill._id)} className="action-button approve">Approve</button>
                                        <button onClick={() => handleReject(bill._id)} className="action-button reject">Reject</button>
                                    </>
                                )}
                            </td>
                        </tr>
                    )) : (
                        <tr>
                            <td colSpan="6">No bill requests found.</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default BillRequests;
