          font-size: 16px;
          font-weight: 600;
          color: #343a40;
        }

        .location-status {
          display: flex;
          align-items: center;
          gap: 8px;
          padding: 12px;
          border-radius: 8px;
          margin-bottom: 8px;
        }

        .location-status.success {
          background: #d4edda;
          color: #155724;
        }

        .location-status.warning {
          background: #fff3cd;
          color: #856404;
        }

        .location-status.error {
          background: #f8d7da;
          color: #721c24;
        }

        .location-details {
          display: flex;
          flex-direction: column;
          margin-left: auto;
        }

