import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    // Validate token on app start
    useEffect(() => {
        validateToken();
    }, []);

    const validateToken = async () => {
        const token = localStorage.getItem('token');
        
        if (!token) {
            setLoading(false);
            setIsAuthenticated(false);
            return;
        }

        try {
            // Set default authorization header
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            
            const response = await axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/validate-token`);
            
            if (response.data.valid) {
                setUser(response.data.user);
                setIsAuthenticated(true);
                
                // Check if registration is complete
                const regResponse = await axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/registration-status`);
                
                if (!regResponse.data.isComplete) {
                    // Registration incomplete, log out user
                    logout();
                    return;
                }
            } else {
                logout();
            }
        } catch (error) {
            console.error('Token validation failed:', error);
            logout();
        } finally {
            setLoading(false);
        }
    };

    const login = async (email, password) => {
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/login`, {
                email,
                password
            });

            const { token, user: userData } = response.data;
            
            // Store token and user data
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(userData));
            
            // Set authorization header
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            
            // Check registration status
            const regResponse = await axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/registration-status`);
            
            if (!regResponse.data.isComplete) {
                logout();
                throw new Error('Registration incomplete. Please complete your profile first.');
            }

            setUser(userData);
            setIsAuthenticated(true);
            
            return { success: true, user: userData };
        } catch (error) {
            throw error;
        }
    };

    const register = async (userData) => {
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/register`, userData);
            
            const { token, user: newUser } = response.data;
            
            // Store token and user data
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(newUser));
            
            // Set authorization header
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            
            setUser(newUser);
            setIsAuthenticated(true);
            
            return { success: true, user: newUser };
        } catch (error) {
            throw error;
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        delete axios.defaults.headers.common['Authorization'];
        setUser(null);
        setIsAuthenticated(false);
    };

    const googleSignIn = async (credential) => {
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/google-signin`, {
                token: credential
            });

            const { token, user: userData } = response.data;
            
            // Store token and user data
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(userData));
            
            // Set authorization header
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            
            setUser(userData);
            setIsAuthenticated(true);
            
            return { success: true, user: userData };
        } catch (error) {
            throw error;
        }
    };

    const value = {
        user,
        loading,
        isAuthenticated,
        login,
        register,
        logout,
        googleSignIn,
        validateToken
    };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};
