import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MySubmissions = () => {
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSubmissions = async () => {
      try {
        const token = localStorage.getItem('token');
        const config = {
          headers: { Authorization: `Bearer ${token}` }
        };
        const res = await axios.get('/api/users/my-submissions', config);
        setSubmissions(res.data);
      } catch (err) {
        setError('Failed to fetch submissions.');
      } finally {
        setLoading(false);
      }
    };

    fetchSubmissions();
  }, []);

  if (loading) return <p>‚è≥ Loading submissions...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  const formatAmount = (amt) =>
    new Intl.NumberFormat('en-IN', { style: 'currency', currency: 'INR' }).format(amt);

  return (
    <div style={{ padding: '20px' }}>
      <h2>üìú My Submission History</h2>
      {submissions.length === 0 ? (
        <p>You have not made any submissions yet.</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '10px' }}>
          <thead>
            <tr style={{ backgroundColor: '#f0f0f0' }}>
              <th style={thStyle}>Date</th>
              <th style={thStyle}>Volume (L)</th>
              <th style={thStyle}>DRC %</th>
              <th style={thStyle}>Amount</th>
              <th style={thStyle}>Status</th>
            </tr>
          </thead>
          <tbody>
            {submissions.map(sub => (
              <tr key={sub._id}>
                <td style={tdStyle}>{new Date(sub.createdAt).toLocaleDateString()}</td>
                <td style={tdStyle}>{sub.latexVolume}</td>
                <td style={tdStyle}>{sub.drcPercentage}</td>
                <td style={tdStyle}>{formatAmount(sub.calculatedAmount)}</td>
                <td style={{ ...tdStyle, color: getStatusColor(sub.status), fontWeight: 'bold' }}>
                  {sub.status}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const thStyle = { padding: '8px', border: '1px solid #ccc', textAlign: 'center' };
const tdStyle = { padding: '8px', border: '1px solid #ddd', textAlign: 'center' };

const getStatusColor = (status) => {
  switch (status.toLowerCase()) {
    case 'approved': return 'green';
    case 'pending': return 'orange';
    case 'rejected': return 'red';
    default: return 'black';
  }
};

export default MySubmissions;
