import React from 'react';
import { Navigate } from 'react-router-dom';

const GuestRoute = ({ children }) => {
    const token = localStorage.getItem('token');
    
    // If the user is logged in (token exists), redirect them to their dashboard
    if (token) {
        // You might want to check the user's role and redirect accordingly
        // For simplicity, we'll just redirect to the user home page.
        return <Navigate to="/user/home" />;
    }

    // If the user is not logged in, show the page
    return children;
};

export default GuestRoute;
