import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import EnquiryWizard from '../components/buying/EnquiryWizard';
import './BuyingPage.css';

// Simple mock data for Rubber Band variants
const PRODUCTS = [
  {
    id: 'rb-std',
    name: 'Rubber Band - Standard',
    desc: 'Durable, everyday use rubber bands. Ideal for office and home.',
    price: 49,
    unit: 'per pack',
    image: '/images/holy1.jpg',
  },
  {
    id: 'rb-prem',
    name: 'Rubber Band - Premium',
    desc: 'High elasticity premium bands for stronger hold.',
    price: 79,
    unit: 'per pack',
    image: '/images/holy2.jpg',
  },
  {
    id: 'rb-industrial',
    name: 'Rubber Band - Industrial',
    desc: 'Heavy-duty bands suitable for packaging and logistics.',
    price: 129,
    unit: 'per pack',
    image: '/images/holy3.jpg',
  },
];

const CART_STORAGE_KEY = 'buying_cart';

const BuyingPage = () => {
  const [cart, setCart] = useState({}); // { productId: quantity }
  const [search, setSearch] = useState('');
  const [wizardOpen, setWizardOpen] = useState(false);
  const navigate = useNavigate();
  const { isAuthenticated, user } = useAuth();

  // Restore cart from localStorage so users don't lose selections after login
  useEffect(() => {
    try {
      const saved = localStorage.getItem(CART_STORAGE_KEY);
      if (saved) {
        const parsed = JSON.parse(saved);
        if (parsed && typeof parsed === 'object') setCart(parsed);
      }
    } catch (_) {}
  }, []);

  // Persist cart on change
  useEffect(() => {
    try {
      localStorage.setItem(CART_STORAGE_KEY, JSON.stringify(cart));
    } catch (_) {}
  }, [cart]);

  const addToCart = (productId) => {
    setCart((prev) => ({ ...prev, [productId]: (prev[productId] || 0) + 1 }));
  };

  const updateQty = (productId, qty) => {
    const q = Math.max(0, Number(qty) || 0);
    setCart((prev) => ({ ...prev, [productId]: q }));
  };

  const removeItem = (productId) => {
    setCart((prev) => {
      const next = { ...prev };
      delete next[productId];
      return next;
    });
  };

  const items = Object.entries(cart).filter(([_, q]) => q > 0);
  const subtotal = items.reduce((sum, [id, q]) => {
    const p = PRODUCTS.find((x) => x.id === id);
    return sum + (p ? p.price * q : 0);
  }, 0);

  const handleCheckout = () => {
    // If not logged in, redirect to login and return here afterwards
    if (!isAuthenticated) {
      try { localStorage.setItem(CART_STORAGE_KEY, JSON.stringify(cart)); } catch (_) {}
      navigate('/login', { state: { from: '/buying' } });
      return;
    }
    // Open the 5-step wizard for enquiry submission
    setWizardOpen(true);
  };

  const filteredProducts = PRODUCTS.filter((p) => {
    const q = search.trim().toLowerCase();
    if (!q) return true;
    return (
      p.name.toLowerCase().includes(q) ||
      p.desc.toLowerCase().includes(q)
    );
  });

  const cartItemsForWizard = useMemo(() => items.map(([id, qty]) => ({ id, qty })), [items]);

  return (
    <div className="buying-page">
      <header className="buying-hero">
        <div className="buying-hero__content">
          <h1>Buy Rubber Bands</h1>
          <p>High quality rubber bands from Holy Family Polymers. Choose a variant and quantity to proceed.</p>
          <a className="btn-primary" href="#products">Shop Now</a>
        </div>
      </header>

      {/* Search box like marketplace search */}
      <section className="buying-search">
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Search products (e.g., premium, industrial)"
          className="search-input"
          aria-label="Search products"
        />
      </section>

      <section id="products" className="product-grid">
        {filteredProducts.length === 0 ? (
          <div className="muted">No products match your search.</div>
        ) : (
          filteredProducts.map((p) => (
            <div key={p.id} className="product-card">
              <div className="product-image">
                <img src={p.image} alt={p.name} />
              </div>
              <div className="product-info">
                <h3>{p.name}</h3>
                <p className="product-desc">{p.desc}</p>
                <div className="product-meta">
                  <span className="product-price">₹{p.price} <small>{p.unit}</small></span>
                </div>
              </div>
              <div className="product-actions">
                <button className="btn-secondary" onClick={() => addToCart(p.id)}>Add to Cart</button>
                <div className="qty-row">
                  <label htmlFor={`qty-${p.id}`}>Qty</label>
                  <input
                    id={`qty-${p.id}`}
                    type="number"
                    min="0"
                    value={cart[p.id] || 0}
                    onChange={(e) => updateQty(p.id, e.target.value)}
                  />
                </div>
              </div>
            </div>
          ))
        )}
      </section>

      <aside className="cart-panel">
        <h2>Your Cart</h2>
        {items.length === 0 ? (
          <p className="muted">Your cart is empty.</p>
        ) : (
          <ul className="cart-list">
            {items.map(([id, q]) => {
              const p = PRODUCTS.find((x) => x.id === id);
              if (!p) return null;
              return (
                <li key={id} className="cart-item">
                  <div>
                    <strong>{p.name}</strong>
                    <div className="muted">₹{p.price} × {q}</div>
                  </div>
                  <div className="cart-item__right">
                    <span className="cart-item__total">₹{p.price * q}</span>
                    <button className="link danger" onClick={() => removeItem(id)}>Remove</button>
                  </div>
                </li>
              );
            })}
          </ul>
        )}

        <div className="cart-summary">
          <div className="row">
            <span>Subtotal</span>
            <strong>₹{subtotal}</strong>
          </div>
          <p className="muted small">Prices shown are indicative. We will confirm availability and shipping after you submit.</p>
          <button className="btn-primary w-100" disabled={items.length === 0} onClick={handleCheckout}>
            {isAuthenticated ? 'Proceed to Enquiry' : 'Login to Submit'}
          </button>
        </div>
      </aside>

      <EnquiryWizard
        open={wizardOpen}
        onClose={() => setWizardOpen(false)}
        cartItems={cartItemsForWizard}
        products={PRODUCTS}
        user={user}
      />
    </div>
  );
};

export default BuyingPage;
