import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../../context/CartContext';
import { placeOrder } from '../../services/storeService';
import './buyers.css';

const CheckoutPage = () => {
  const { items, clearCart, totals } = useCart();
  const [notes, setNotes] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const submit = async () => {
    setLoading(true);
    const payload = {
      items: items.map(i => ({
        product: i.productId,
        name: i.name,
        type: i.type,
        category: i.category,
        unitPrice: i.price,
        quantity: i.quantity,
        subtotal: i.price * i.quantity,
      })),
      notes,
    };
    const res = await placeOrder(payload);
    setLoading(false);
    if (res?.success) {
      clearCart();
      navigate('/buyers/orders');
    } else {
      alert(res?.message || 'Failed to place order');
    }
  };

  return (
    <div className="buyers-container">
      <div className="buyers-header">
        <h1>Checkout</h1>
        <p>Total: â‚¹{totals.subtotal}</p>
      </div>

      {items.length === 0 ? (
        <div className="buyers-empty">Your cart is empty.</div>
      ) : (
        <div className="checkout-panel">
          <label>Notes (optional)</label>
          <textarea value={notes} onChange={(e) => setNotes(e.target.value)} placeholder="Any special request..." />
          <button className="buyers-btn primary" onClick={submit} disabled={loading}>
            {loading ? 'Placing order...' : 'Place Order'}
          </button>
        </div>
      )}
    </div>
  );
};

export default CheckoutPage;
