import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './BarrelLogistics.css';

const BarrelLogistics = () => {
  const [barrels, setBarrels] = useState([]);
  const [loading, setLoading] = useState(true);
  // eslint-disable-next-line no-unused-vars
  const [showAddModal, setShowAddModal] = useState(false);
  // eslint-disable-next-line no-unused-vars
  const [showUpdateModal, setShowUpdateModal] = useState(false);
  // eslint-disable-next-line no-unused-vars
  const [selectedBarrel, setSelectedBarrel] = useState(null);
  // eslint-disable-next-line no-unused-vars
  const [formData, setFormData] = useState({
    barrelId: '',
    capacity: '',
    currentVolume: '',
    status: 'in-storage',
    lastKnownLocation: '',
    notes: ''
  });

  // Fetch all barrels
  const fetchBarrels = async () => {
    try {
      const response = await axios.get('/api/barrels', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setBarrels(response.data);
    } catch (error) {
      console.error('Error fetching barrels:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBarrels();
  }, []);

  if (loading) {
    return (
      <div className="barrel-logistics-container">
        <div className="loading-spinner">Loading barrels...</div>
      </div>
    );
  }

  return (
    <div className="barrel-logistics-container">
      <div className="page-header">
        <h1>Barrel Logistics Management</h1>
        <button 
          className="btn btn-primary"
          onClick={() => setShowAddModal(true)}
        >
          Add New Barrel
        </button>
      </div>

      {/* Statistics Cards */}
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Barrels</h3>
          <p className="stat-number">{barrels.length}</p>
        </div>
        <div className="stat-card">
          <h3>In Use</h3>
          <p className="stat-number">{barrels.filter(b => b.status === 'in-use').length}</p>
        </div>
        <div className="stat-card">
          <h3>In Storage</h3>
          <p className="stat-number">{barrels.filter(b => b.status === 'in-storage').length}</p>
        </div>
        <div className="stat-card">
          <h3>Disposed</h3>
          <p className="stat-number">{barrels.filter(b => b.status === 'disposed').length}</p>
        </div>
      </div>

      {/* Barrels Table */}
      <div className="barrels-table-container">
        <table className="barrels-table">
          <thead>
            <tr>
              <th>#</th>
              <th>Barrel ID</th>
              <th>Capacity (L)</th>
              <th>Current Volume (L)</th>
              <th>Fill %</th>
              <th>Status</th>
              <th>Location</th>
              <th>Last Updated</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {barrels.map((barrel, index) => (
              <tr key={barrel._id}>
                <td>{index}</td>
                <td className="barrel-id">{barrel.barrelId}</td>
                <td>{barrel.capacity}</td>
                <td>{barrel.currentVolume}</td>
                <td>
                  <div className="fill-indicator">
                    <div 
                      className="fill-bar"
                      style={{ 
                        width: `${Math.round((barrel.currentVolume / barrel.capacity) * 100)}%`,
                        backgroundColor: Math.round((barrel.currentVolume / barrel.capacity) * 100) > 80 ? '#e74c3c' : 
                                       Math.round((barrel.currentVolume / barrel.capacity) * 100) > 60 ? '#f39c12' : '#27ae60'
                      }}
                    ></div>
                    <span>{Math.round((barrel.currentVolume / barrel.capacity) * 100)}%</span>
                  </div>
                </td>
                <td>
                  <span 
                    className="status-badge"
                    style={{ 
                      backgroundColor: barrel.status === 'in-use' ? '#f39c12' : 
                                     barrel.status === 'in-storage' ? '#27ae60' : 
                                     barrel.status === 'disposed' ? '#e74c3c' : '#95a5a6'
                    }}
                  >
                    {barrel.status}
                  </span>
                </td>
                <td>{barrel.lastKnownLocation || 'N/A'}</td>
                <td>{new Date(barrel.updatedAt).toLocaleDateString()}</td>
                <td>
                  <button 
                    className="btn btn-sm btn-secondary"
                    onClick={() => {
                      setSelectedBarrel(barrel);
                      setFormData({
                        barrelId: barrel.barrelId,
                        capacity: barrel.capacity,
                        currentVolume: barrel.currentVolume,
                        status: barrel.status,
                        lastKnownLocation: barrel.lastKnownLocation,
                        notes: barrel.notes || ''
                      });
                      setShowUpdateModal(true);
                    }}
                  >
                    Update
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default BarrelLogistics;
