import React, { useState } from "react";

const LiveRatePage = () => {
  const [rate, setRate] = useState("");
  const [updatedRate, setUpdatedRate] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Normally send rate to backend (API call)
    console.log("New Rate Updated:", rate);

    setUpdatedRate(rate);
    setRate("");
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Admin - Update Live Rate</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="number"
          placeholder="Enter new rate"
          value={rate}
          onChange={(e) => setRate(e.target.value)}
          className="border p-2 rounded w-full"
          required
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Update Rate
        </button>
      </form>

      {updatedRate && (
        <div className="mt-4 p-3 bg-green-100 rounded">
          âœ… Current Live Rate: <strong>{updatedRate}</strong>
        </div>
      )}
    </div>
  );
};

export default LiveRatePage;
