import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const RegisterPage = () => {
    const [formData, setFormData] = useState({ name: '', email: '', phoneNumber: '', password: '' });
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const { name, email, phoneNumber, password } = formData;
    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    // ... validation logic is the same ...
    const validateForm = () => {
        const emailRegex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
        if (!name || !email || !phoneNumber || !password) {
            setError('Please fill out all fields.');
            return false;
        }
        if (!emailRegex.test(email)) {
            setError('Please enter a valid email address.');
            return false;
        }
        if (password.length < 6) {
            setError('Password must be at least 6 characters long.');
            return false;
        }
        setError('');
        return true;
    };

    const onSubmit = async e => {
        e.preventDefault();
        if (validateForm()) {
            try {
                await axios.post('/api/auth/register', formData);
                alert('Registration successful! Please log in.');
                navigate('/login');
            } catch (err) {
                setError(err.response?.data?.message || 'An error occurred during registration.');
            }
        }
    };

    return (
        <div className="auth-wrapper">
            <div className="form-container">
                <h2>Register Your Account</h2>
                {error && <div className="error-message">{error}</div>}
                <form onSubmit={onSubmit}>
                    <div className="input-group">
                        <input className="form-input" type="text" placeholder="Full Name" name="name" value={name} onChange={onChange} />
                    </div>
                    <div className="input-group">
                        <input className="form-input" type="email" placeholder="Email Address" name="email" value={email} onChange={onChange} />
                    </div>
                    <div className="input-group">
                        <input className="form-input" type="text" placeholder="Phone Number" name="phoneNumber" value={phoneNumber} onChange={onChange} />
                    </div>
                    <div className="input-group">
                        <input className="form-input" type="password" placeholder="Password" name="password" value={password} onChange={onChange} />
                    </div>
                    <button className="form-button" type="submit">Register</button>
                </form>
                <p className="form-text">
                    Already have an account? <Link to="/login">Login here</Link>
                </p>
            </div>
        </div>
    );
};

export default RegisterPage;
