import React from 'react';
import { Link } from 'react-router-dom';
import Slider from 'react-slick';
import './HomePage.css';

// Import slick-carousel CSS
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

const HomePage = () => {
    // Settings for the slider
    const sliderSettings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000, // Changes slide every 3 seconds
        fade: true,
        cssEase: 'linear'
    };

    return (
        <div>
            {/* NEW: Image Slider Section */}
            <header className="slider-container">
                <Slider {...sliderSettings}>
                    {/* Slide 1 */}
                    <div className="slide slide-1">
                        <div className="slide-content">
                            <h1 className="hero-title">Quality Natural Latex</h1>
                            <p className="hero-subtitle">Sustainably sourced and processed for superior rubber products.</p>
                            <Link to="/about" className="cta-button cta-primary">Learn More</Link>
                        </div>
                    </div>
                    {/* Slide 2 */}
                    <div className="slide slide-2">
                        <div className="slide-content">
                            <h1 className="hero-title">Streamlined Digital Platform</h1>
                            <p className="hero-subtitle">Manage your submissions with efficiency and transparency.</p>
                            <Link to="/register" className="cta-button cta-primary">Get Started</Link>
                        </div>
                    </div>
                    {/* Slide 3 */}
                    <div className="slide slide-3">
                        <div className="slide-content">
                            <h1 className="hero-title">Partnering with Local Farmers</h1>
                            <p className="hero-subtitle">Committed to fair trade and empowering our community in Kottayam.</p>
                            <Link to="/contact" className="cta-button cta-primary">Contact Us</Link>
                        </div>
                    </div>
                </Slider>
            </header>

            {/* Features Section */}
            <section className="features-section">
                {/* ... your existing features section ... */}
            </section>

            {/* Footer */}
            <footer className="footer">
                <p>&copy; 2025 Holy Family Polymers. All Rights Reserved.</p>
            </footer>
        </div>
    );
};

export default HomePage;
