import React, { useEffect, useState } from 'react';
import { useAuth } from '../../context/AuthContext';

function DocsList({ token, onAdd, onRemove }) {
  const [docs, setDocs] = useState([]);
  const [label, setLabel] = useState('');
  const [url, setUrl] = useState('');
  const [docType, setDocType] = useState('Aadhaar'); // Aadhaar, Health Card, Photo
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const base = process.env.REACT_APP_API_URL || 'http://localhost:5000';

  useEffect(() => {
    const load = async () => {
      const res = await fetch(`${base}/api/workers/me`, { headers: { Authorization: `Bearer ${token}` } });
      if (res.ok) {
        const json = await res.json();
        setDocs(json.documents || []);
      }
    };
    load();
  }, [token]);

  const add = async () => {
    // If file chosen, upload it first to get URL
    let finalUrl = url;
    if (file) {
      try {
        setUploading(true);
        const form = new FormData();
        form.append('file', file);
        const res = await fetch(`${base}/api/uploads/document`, {
          method: 'POST',
          headers: { Authorization: `Bearer ${token}` },
          body: form,
        });
        if (!res.ok) {
          const msg = await res.text();
          throw new Error(msg || 'Upload failed');
        }
        const data = await res.json();
        finalUrl = data?.file?.path ? `${base}${data.file.path}` : finalUrl;
      } catch (e) {
        alert(e.message || 'File upload failed');
        setUploading(false);
        return;
      } finally {
        setUploading(false);
      }
    }

    await onAdd(label || docType, finalUrl);
    setLabel(''); setUrl(''); setFile(null);
    const res = await fetch(`${base}/api/workers/me`, { headers: { Authorization: `Bearer ${token}` } });
    if (res.ok) {
      const json = await res.json();
      setDocs(json.documents || []);
    }
  };

  const remove = async (i) => {
    await onRemove(i);
    const res = await fetch(`${base}/api/workers/me`, { headers: { Authorization: `Bearer ${token}` } });
    if (res.ok) {
      const json = await res.json();
      setDocs(json.documents || []);
    }
  };

  return (
    <div>
      <div style={{ display:'flex', flexWrap:'wrap', gap:8, marginBottom:12 }}>
        {/* Type selector */}
        <select className="form-control" value={docType} onChange={(e)=>setDocType(e.target.value)} style={{ maxWidth: 180 }}>
          <option value="Aadhaar">Aadhaar</option>
          <option value="Health Card">Health Card</option>
          <option value="Photo">Photo</option>
          <option value="Other">Other</option>
        </select>

        {/* Optional label */}
        <input className="form-control" placeholder="Label (optional)" value={label} onChange={(e)=>setLabel(e.target.value)} />

        {/* Either choose a file OR paste a URL */}
        <input className="form-control" type="file" accept=".jpg,.jpeg,.png,.gif,.webp,.pdf" onChange={(e)=>setFile(e.target.files?.[0] || null)} style={{ maxWidth: 280 }} />
        <input className="form-control" placeholder="Document URL (optional if file chosen)" value={url} onChange={(e)=>setUrl(e.target.value)} />

        <button className="btn btn-primary" onClick={add} disabled={uploading}>
          {uploading ? 'Uploading...' : 'Upload'}
        </button>
      </div>
      <ul className="list-group">
        {docs.map((d, i) => (
          <li key={i} className="list-group-item d-flex justify-content-between align-items-center">
            <span>
              {d.label ? `${d.label} - ` : ''}
              <a href={d.url} target="_blank" rel="noreferrer">{d.url}</a>
            </span>
            <button className="btn btn-sm btn-outline-danger" onClick={()=>remove(i)}>Remove</button>
          </li>
        ))}
        {docs.length === 0 && <li className="list-group-item">No documents</li>}
      </ul>
    </div>
  );
}

const StaffProfile = () => {
  const { user } = useAuth();
  const [worker, setWorker] = useState(null);
  const [activeTab, setActiveTab] = useState('personal');
  const [saving, setSaving] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [showStaffInfo, setShowStaffInfo] = useState(false);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchWorker = async () => {
      const base = process.env.REACT_APP_API_URL || 'http://localhost:5000';
      try {
        setLoading(true);
        const res = await fetch(`${base}/api/workers/me`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (res.ok) {
          const json = await res.json();
          setWorker({
            name: json.name || json.user?.name,
            email: json.user?.email,
            dailyWage: json.dailyWage,
            origin: json.origin,
            contactNumber: json.contactNumber,
            role: json.role,
            staffId: json.staffId || json._id,
            address: json.address,
            emergencyContactName: json.emergencyContactName,
            emergencyContactNumber: json.emergencyContactNumber,
            dateOfBirth: json.dateOfBirth,
            aadhaarNumber: json.aadhaarNumber,
            photoUrl: json.photoUrl,
            health: json.health,
          });
          setError(null);
          return;
        }
        // If worker profile is missing (e.g., 404), fall back to basic user profile
        const userRes = await fetch(`${base}/api/users/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (userRes.ok) {
          const u = await userRes.json();
          setWorker({
            name: u.name,
            email: u.email,
            dailyWage: 0,
            origin: u.location || '-',
            contactNumber: u.phoneNumber || '-',
            role: u.role || 'staff',
            staffId: u._id,
            address: u.address || '',
            emergencyContactName: '',
            emergencyContactNumber: '',
            dateOfBirth: '',
            aadhaarNumber: '',
            photoUrl: '',
            health: {},
          });
          setError(null);
        } else {
          throw new Error('Failed to load profile');
        }
      } catch (e) {
        setError(e.message || 'Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchWorker();
    }
  }, [user, token]);

  const base = process.env.REACT_APP_API_URL || 'http://localhost:5000';

  const updateProfile = async (payload) => {
    try {
      setSaving(true);
      setError(null);

      const res = await fetch(`${base}/api/workers/me`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify(payload),
      });

      if (!res.ok) {
        const errorData = await res.json().catch(() => ({ message: 'Failed to save' }));
        throw new Error(errorData.message || `Server error: ${res.status}`);
      }

      const json = await res.json();
      setWorker({
        ...worker,
        name: json.name || json.user?.name,
        email: json.user?.email,
        dailyWage: json.dailyWage,
        origin: json.origin,
        contactNumber: json.contactNumber,
        address: json.address,
        emergencyContactName: json.emergencyContactName,
        emergencyContactNumber: json.emergencyContactNumber,
        dateOfBirth: json.dateOfBirth,
        aadhaarNumber: json.aadhaarNumber,
        photoUrl: json.photoUrl,
        health: json.health,
        staffId: json.staffId || json._id,
      });

      // Show success message briefly
      setSuccess('Profile updated successfully!');
      setTimeout(() => {
        setError(null);
        setSuccess(null);
      }, 3000);
    } catch (e) {
      console.error('Profile update error:', e);
      setError(e.message || 'Failed to save profile');
    } finally {
      setSaving(false);
    }
  };

  const handleNext = () => {
    const tabs = ['personal', 'documents', 'health'];
    const currentIndex = tabs.indexOf(activeTab);
    if (currentIndex < tabs.length - 1) {
      setActiveTab(tabs[currentIndex + 1]);
    } else {
      setShowStaffInfo(true);
    }
  };

  const handleSave = async () => {
    // Validate current tab data before saving
    if (activeTab === 'personal') {
      const nameInput = document.querySelector('input[defaultValue="' + (worker?.name || '') + '"]');
      if (nameInput && !nameInput.value.trim()) {
        setError('Name is required');
        return;
      }
    }

    // Trigger save for current tab
    await updateProfile({});
  };

  const addDocument = async (label, url) => {
    await fetch(`${base}/api/workers/me/documents`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
      body: JSON.stringify({ label, url }),
    });
  };

  const removeDocument = async (idx) => {
    await fetch(`${base}/api/workers/me/documents/${idx}`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` },
    });
  };

  if (loading) return <div style={{ padding: 16 }}>Loading profile...</div>;

  // Display error message if any
  const ErrorMessage = () => error ? (
    <div className="alert alert-danger" style={{ marginBottom: 16 }}>
      <i className="fas fa-exclamation-triangle"></i> {error}
    </div>
  ) : null;

  // Display success message if any
  const SuccessMessage = () => success ? (
    <div className="alert alert-success" style={{ marginBottom: 16 }}>
      <i className="fas fa-check-circle"></i> {success}
    </div>
  ) : null;

  // Staff Information Display Component
  const StaffInfoDisplay = () => (
    <div style={{ 
      marginTop: 24, 
      padding: 20, 
      backgroundColor: '#f8f9fa', 
      border: '1px solid #dee2e6', 
      borderRadius: 8,
      maxWidth: 640
    }}>
      <h3 style={{ color: '#495057', marginBottom: 16 }}>Staff Information Summary</h3>
      <div style={{ display: 'grid', gap: 12 }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderBottom: '1px solid #e9ecef' }}>
          <strong>Staff ID:</strong>
          <span style={{ color: '#6c757d' }}>{worker?.staffId || 'Not assigned'}</span>
        </div>
        <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderBottom: '1px solid #e9ecef' }}>
          <strong>Name:</strong>
          <span style={{ color: '#6c757d' }}>{worker?.name || 'Not provided'}</span>
        </div>
        <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderBottom: '1px solid #e9ecef' }}>
          <strong>Address:</strong>
          <span style={{ color: '#6c757d', textAlign: 'right', maxWidth: '60%' }}>{worker?.address || 'Not provided'}</span>
        </div>
        <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderBottom: '1px solid #e9ecef' }}>
          <strong>Contact:</strong>
          <span style={{ color: '#6c757d' }}>{worker?.contactNumber || 'Not provided'}</span>
        </div>
        <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderBottom: '1px solid #e9ecef' }}>
          <strong>Email:</strong>
          <span style={{ color: '#6c757d' }}>{worker?.email || 'Not provided'}</span>
        </div>
        <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0' }}>
          <strong>Role:</strong>
          <span style={{ color: '#6c757d' }}>{worker?.role || 'Staff'}</span>
        </div>
      </div>
      <div style={{ marginTop: 16, display: 'flex', gap: 12 }}>
        <button 
          className="btn btn-secondary" 
          onClick={() => setShowStaffInfo(false)}
        >
          Edit Profile
        </button>
        <button 
          className="btn btn-success" 
          onClick={() => window.print()}
        >
          Print Profile
        </button>
      </div>
    </div>
  );

  return (
    <div style={{ padding: 16 }}>
      <h2>My Profile</h2>

      <ErrorMessage />
      <SuccessMessage />

      {showStaffInfo ? (
        <StaffInfoDisplay />
      ) : (
        <>
          {/* Tabs */}
          <div style={{ display: 'flex', gap: 12, marginTop: 12 }}>
            <button className={`btn btn-${activeTab==='personal'?'primary':'light'}`} onClick={()=>setActiveTab('personal')}>Personal</button>
            <button className={`btn btn-${activeTab==='documents'?'primary':'light'}`} onClick={()=>setActiveTab('documents')}>Documents</button>
            <button className={`btn btn-${activeTab==='health'?'primary':'light'}`} onClick={()=>setActiveTab('health')}>Health</button>
          </div>
        </>
      )}

      {activeTab === 'personal' && (
        <div style={{ marginTop: 16, maxWidth: 640 }}>
          <div className="form-group">
            <label>Name</label>
            <input className="form-control" defaultValue={worker?.name||''} onBlur={(e)=>updateProfile({ name: e.target.value })} />
          </div>
          <div className="form-group">
            <label>Contact Number</label>
            <input className="form-control" defaultValue={worker?.contactNumber||''} onBlur={(e)=>updateProfile({ contactNumber: e.target.value })} />
          </div>
          <div className="form-group">
            <label>Address</label>
            <textarea className="form-control" defaultValue={worker?.address||''} onBlur={(e)=>updateProfile({ address: e.target.value })} />
          </div>
          <div className="form-row" style={{ display:'flex', gap:12 }}>
            <div className="form-group" style={{ flex:1 }}>
              <label>Emergency Contact Name</label>
              <input className="form-control" defaultValue={worker?.emergencyContactName||''} onBlur={(e)=>updateProfile({ emergencyContactName: e.target.value })} />
            </div>
            <div className="form-group" style={{ flex:1 }}>
              <label>Emergency Contact Number</label>
              <input className="form-control" defaultValue={worker?.emergencyContactNumber||''} onBlur={(e)=>updateProfile({ emergencyContactNumber: e.target.value })} />
            </div>
          </div>
          <div className="form-row" style={{ display:'flex', gap:12 }}>
            <div className="form-group" style={{ flex:1 }}>
              <label>Date of Birth</label>
              <input type="date" className="form-control" defaultValue={worker?.dateOfBirth? String(worker.dateOfBirth).slice(0,10):''} onBlur={(e)=>updateProfile({ dateOfBirth: e.target.value })} />
            </div>
            <div className="form-group" style={{ flex:1 }}>
              <label>Origin</label>
              <select className="form-control" defaultValue={worker?.origin||'kerala'} onChange={(e)=>updateProfile({ origin: e.target.value })}>
                <option value="kerala">Kerala</option>
                <option value="other">Other State</option>
              </select>
            </div>
          </div>
          <div className="form-row" style={{ display:'flex', gap:12 }}>
            <div className="form-group" style={{ flex:1 }}>
              <label>Aadhaar Number</label>
              <input className="form-control" defaultValue={worker?.aadhaarNumber||''} onBlur={(e)=>updateProfile({ aadhaarNumber: e.target.value })} />
            </div>
            <div className="form-group" style={{ flex:1 }}>
              <label>Photo URL</label>
              <input className="form-control" defaultValue={worker?.photoUrl||''} onBlur={(e)=>updateProfile({ photoUrl: e.target.value })} />
            </div>
          </div>
          {/* Action Buttons */}
          <div style={{ marginTop: 24, display: 'flex', gap: 12 }}>
            <button 
              className="btn btn-primary" 
              onClick={handleSave}
              disabled={saving}
              style={{ minWidth: 100 }}
            >
              {saving ? (
                <>
                  <i className="fas fa-spinner fa-spin" style={{ marginRight: 8 }}></i>
                  Saving...
                </>
              ) : (
                <>
                  <i className="fas fa-save" style={{ marginRight: 8 }}></i>
                  Save
                </>
              )}
            </button>
            <button 
              className="btn btn-success" 
              onClick={handleNext}
              disabled={saving}
              style={{ minWidth: 100 }}
            >
              <i className="fas fa-arrow-right" style={{ marginRight: 8 }}></i>
              Next
            </button>
          </div>
        </div>
      )}

      {activeTab === 'documents' && (
        <div style={{ marginTop: 16 }}>
          <DocsList token={token} onAdd={addDocument} onRemove={removeDocument} />

          {/* Action Buttons */}
          <div style={{ marginTop: 24, display: 'flex', gap: 12 }}>
            <button 
              className="btn btn-primary" 
              onClick={handleSave}
              disabled={saving}
              style={{ minWidth: 100 }}
            >
              {saving ? (
                <>
                  <i className="fas fa-spinner fa-spin" style={{ marginRight: 8 }}></i>
                  Saving...
                </>
              ) : (
                <>
                  <i className="fas fa-save" style={{ marginRight: 8 }}></i>
                  Save
                </>
              )}
            </button>
            <button 
              className="btn btn-success" 
              onClick={handleNext}
              disabled={saving}
              style={{ minWidth: 100 }}
            >
              <i className="fas fa-arrow-right" style={{ marginRight: 8 }}></i>
              Next
            </button>
          </div>
        </div>
      )}

      {activeTab === 'health' && (
        <div style={{ marginTop: 16, maxWidth: 640 }}>
          <div className="form-row" style={{ display:'flex', gap:12 }}>
            <div className="form-group" style={{ flex:1 }}>
              <label>Blood Group</label>
              <input className="form-control" defaultValue={worker?.health?.bloodGroup||''} onBlur={(e)=>updateProfile({ health: { ...worker?.health, bloodGroup: e.target.value } })} />
            </div>
            <div className="form-group" style={{ flex:1 }}>
              <label>Medical Certificate URL</label>
              <input className="form-control" defaultValue={worker?.health?.medicalCertificateUrl||''} onBlur={(e)=>updateProfile({ health: { ...worker?.health, medicalCertificateUrl: e.target.value } })} />
            </div>
          </div>
          <div className="form-row" style={{ display:'flex', gap:12 }}>
            <div className="form-group" style={{ flex:1 }}>
              <label>Last Checkup Date</label>
              <input type="date" className="form-control" defaultValue={worker?.health?.lastCheckupDate? String(worker.health.lastCheckupDate).slice(0,10):''} onBlur={(e)=>updateProfile({ health: { ...worker?.health, lastCheckupDate: e.target.value } })} />
            </div>
          </div>
          <div className="form-group">
            <label>Notes</label>
            <textarea className="form-control" defaultValue={worker?.health?.notes||''} onBlur={(e)=>updateProfile({ health: { ...worker?.health, notes: e.target.value } })} />
          </div>

          {/* Action Buttons */}
          <div style={{ marginTop: 24, display: 'flex', gap: 12 }}>
            <button 
              className="btn btn-primary" 
              onClick={handleSave}
              disabled={saving}
            >
              {saving ? 'Saving...' : 'Save'}
            </button>
            <button 
              className="btn btn-success" 
              onClick={handleNext}
              disabled={saving}
              style={{ minWidth: 120 }}
            >
              <i className="fas fa-eye" style={{ marginRight: 8 }}></i>
              View Summary
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default StaffProfile;
