import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';

// ... inside the LoginPage component
const handleGoogleSuccess = async (credentialResponse) => {
    const token = credentialResponse.credential;
    try {
        const res = await axios.post('/api/auth/google-signin', { token });
        // Handle successful login (store token, redirect)
    } catch (error) {
        setError('Google Sign-In failed.');
    }
};

// ... in the return statement, after your form
<div style={{ textAlign: 'center', margin: '20px 0' }}>
    <GoogleLogin
        onSuccess={handleGoogleSuccess}
        onError={() => {
            console.log('Login Failed');
        }}
    />
</div>

const LoginPage = () => {
    const [formData, setFormData] = useState({ email: '', password: '' });
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const { email, password } = formData;
    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = async e => {
        e.preventDefault();
        if (!email || !password) {
            setError('Please enter both email and password.');
            return;
        }
        try {
            const res = await axios.post('/api/auth/login', formData);
            localStorage.setItem('token', res.data.token);
            localStorage.setItem('user', JSON.stringify(res.data.user));
            if (res.data.user.role === 'admin') {
                navigate('/admin/home');
            } else {
                navigate('/user/home');
            }
        } catch (err) {
            setError(err.response?.data?.message || 'An error occurred during login.');
        }
    };

    return (
        <div className="auth-wrapper">
            <div className="form-container">
                <h2>Login to Your Account</h2>
                {error && <div className="error-message">{error}</div>}
                <form onSubmit={onSubmit}>
                    <div className="input-group">
                        <input className="form-input" type="email" placeholder="Email Address" name="email" value={email} onChange={onChange} required />
                    </div>
                    <div className="input-group">
                        <input className="form-input" type="password" placeholder="Password" name="password" value={password} onChange={onChange} required />
                    </div>
                    <button className="form-button" type="submit">Login</button>
                </form>
                <p className="form-text">
                    Don't have an account? <Link to="/register">Register here</Link>
                </p>
            </div>
        </div>
    );
};

export default LoginPage;
