import React, { useState, useEffect, useCallback, useMemo } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const StockManagement = () => {
    const [transactions, setTransactions] = useState([]);
    const [currentStock, setCurrentStock] = useState(0);
    const [formData, setFormData] = useState({ type: 'in', quantity: '' });

    // ✅ Memoize config so it doesn’t recreate on every render
    const config = useMemo(() => ({
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    }), []);

    // ✅ Fetch transactions
    const fetchTransactions = useCallback(async () => {
        try {
            const { data } = await axios.get('/api/admin/stock', config);
            setTransactions(data);

            // Calculate current stock level
            let stockLevel = 0;
            data.forEach(t => {
                if (t.type === 'in') stockLevel += t.quantity;
                else stockLevel -= t.quantity;
            });
            setCurrentStock(stockLevel);
        } catch (error) {
            console.error("Error fetching stock transactions:", error);
        }
    }, [config]);

    useEffect(() => {
        fetchTransactions();
    }, [fetchTransactions]);

    const onSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/api/admin/stock', formData, config);
            fetchTransactions(); // Refresh list
            setFormData({ type: 'in', quantity: '' });
        } catch (error) {
            console.error("Error adding transaction:", error);
        }
    };

    return (
        <div>
            <h2 className="page-title">Stock Management</h2>

            {/* Current Stock */}
            <div className="stats-cards-grid">
                <div className="stat-card">
                    <div className="card-icon green">
                        <i className="fas fa-boxes"></i>
                    </div>
                    <div className="card-info">
                        <h4>Current Stock (kg)</h4>
                        <p>{currentStock}</p>
                    </div>
                </div>
            </div>

            {/* Add Transaction */}
            <div className="admin-content" style={{ marginTop: '2rem' }}>
                <h3>Add New Transaction</h3>
                <form onSubmit={onSubmit} className="stock-form">
                    <select 
                        value={formData.type} 
                        onChange={e => setFormData({...formData, type: e.target.value})}
                    >
                        <option value="in">Stock In</option>
                        <option value="out">Stock Out</option>
                    </select>
                    <input 
                        type="number" 
                        placeholder="Quantity (kg)" 
                        value={formData.quantity}
                        onChange={e => setFormData({...formData, quantity: e.target.value})}
                        required 
                    />
                    <button type="submit">Add Transaction</button>
                </form>
            </div>

            {/* Transaction History */}
            <div style={{ marginTop: '2rem' }}>
                <h3>Transaction History</h3>
                <table className="transactions-table">
                    <thead>
                        <tr>
                            <th>Type</th>
                            <th>Quantity</th>
                            <th>Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {transactions.map((t) => (
                            <tr key={t._id}>
                                <td>{t.type}</td>
                                <td>{t.quantity}</td>
                                <td>{new Date(t.createdAt).toLocaleString()}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default StockManagement;
